import React, { useState, useEffect } from 'react';
import {
  Text,
  EventTypeText,
  Title,
  OutsideBox,
  InsideBox,
  FilterInput,
  EventBox,
  Button,
  EventTitle,
  EventDescription,
  TimeText,
  DateHeader,
} from './components/components.js'

function App() {
  const [events, setEvents] = useState({});
  const [inputValue, setInputValue] = useState("");
  const [filterValues, setFilterValues] = useState([]);
  const [expandedEvents, setExpandedEvents] = useState([]);


  useEffect(() => {
    function groupByDate(list) {
      return list.reduce(function(accum, current) {
        var property = (new Date(current.start_time)).toLocaleString("en-CA", {weekday: 'long', month: 'long', day: 'numeric'});
        if (!accum[property]) {
          accum[property] = [];
        }
        accum[property].push(current);
        return accum;
      }, {});
    }

    async function fetchEvents() {
      fetch('https://api.hackthenorth.com/v3/graphql?query={ events { id name event_type permission start_time end_time description speakers { name profile_pic } public_url private_url related_events } }')
        .then(response => response.json())
        .then(data => {
          var allEvents = data.data.events
          var filteredEvents = allEvents;
          if (filterValues.length !== 0) {
            // allow user to filter based on search terms
            filteredEvents = allEvents.filter(event => filterValues.some(filterValue => (event.name.toLowerCase()).includes(filterValue.toLowerCase())));
          }
          // sort by start time
          var sortedEvents = filteredEvents.sort((a, b) => (a.start_time - b.start_time))
          var groupedEvents = groupByDate(sortedEvents)
          // setEvents(filteredEvents.sort((a, b) => (a.start_time - b.start_time)))
          setEvents(groupedEvents)
        });
    }

    fetchEvents();

    console.log("expandedEvents: ", expandedEvents)
    document.addEventListener("scroll", e => {
      let scrolled = document.scrollingElement.scrollTop;
      console.log(scrolled)
    })
  }, [expandedEvents])

  function handleFilterChange(e) {
    setInputValue(e.target.value);
    setFilterValues(e.target.value.trim().split(" "));
  }

  function getEventType(type) {
    switch (type) {
      case "workshop":
        return "Workshop"
      case "activity":
        return "Activity"
      case "tech_talk":
        return "Tech Talk"
      default:
        return;
    }
  }

  var currentDate = -1;

  function handleEventClick(e, id) {
    e.preventDefault();
    var expandedCopy = expandedEvents;
    const index = expandedCopy.indexOf(id);
    if (index > -1) {
      expandedCopy.splice(index, 1);
    } else {
      expandedCopy.push(id);
    }
    setExpandedEvents(expandedCopy);
  }

  return (
    <OutsideBox>
      <header>
        <Title>Schedule</Title>
        <FilterInput
          onChange={ handleFilterChange }
          placeholder="Filter schedule by..."
          type="text"
          value={ inputValue }
        />
      </header>
      {Object.keys(events).map((day, i) => {
        return (
          <>
            <DateHeader>{day}</DateHeader>
            <InsideBox key={day}>
              {events[day].map((event, i) => {
                var start = new Date(event.start_time);
                var end = new Date(event.end_time);
                var startTime = start.toLocaleString("en-CA", {hour: 'numeric', minute: '2-digit'});
                var endTime = end.toLocaleString("en-CA", {hour: 'numeric', minute: '2-digit'});
                var dateText;
                if (currentDate !== start.getDate()) {
                  currentDate = start.getDate();
                  dateText = start.toLocaleString("en-CA", {weekday: 'long', month: 'long', day: 'numeric'});
                }
                console.log("i:", i)
                return (
                  <a href="#" onClick={ (e) => handleEventClick(e, event.id) }>
                    <EventBox key={event.id} first={i === 0 ? true : false}>
                      <EventTypeText type={event.event_type}>{getEventType(event.event_type)}</EventTypeText>
                      <EventTitle type={event.event_type}>{event.name}</EventTitle>
                      <Text>{startTime} - {endTime}</Text>
                      {/* <Text hidden={expandedEvents.includes(event.id)}>{event.description}</Text> */}
                      {(expandedEvents.includes(event.id)) && <Text>{event.description}</Text>}
                    </EventBox>
                  </a>
                )
              })}
            </InsideBox>
          </>
        )
      })}
    </OutsideBox>
  );
}

export default App;
