import styled from 'styled-components';

export const Text = styled.p`
  color: ${props => props.theme.colors.darkBlue}
`

export const Title = styled.h1`
  font-size: 4em;
  color: ${props => props.theme.colors.darkBlue};
`

export const OutsideBox = styled.div`
  margin: 10% 20%;
`

export const InsideBox = styled.div`

`

export const FilterInput = styled.input`
  padding: 5px;
  float: right;
  border: 2px solid ${props => props.theme.colors.darkBlue};
  font-size: 1.1em;
`

export const EventBox = styled.div`
  border: 2px solid ${props => props.theme.colors.darkBlue};
  border-radius: 10px;
  padding: 20px;
`

export const Button = styled.a`
  padding: 10px;
  border: 2px solid ${props => props.theme.colors.darkBlue};
  border-radius: 10px;
  cursor: pointer;
  ${props => {
    switch (props.children) {
      case "Workshop":
        return `color: ${props.theme.colors.darkBeige};`
      case "Activity":
        return `color: ${props.theme.colors.tartOrange};`
      case "Tech Talk":
        return `color: ${props.theme.colors.teal};`
      default:
        return `color: ${props.theme.colors.darkBlue};`
    }
  }};

  &:hover {
    color: #143867;
  }
`

export const EventTitle = styled.h3`
  ${props => {
    switch (props.type) {
      case "workshop":
        return `color: ${props.theme.colors.darkBeige};`
      case "activity":
        return `color: ${props.theme.colors.tartOrange};`
      case "tech_talk":
        return `color: ${props.theme.colors.teal};`
      default:
        return `color: ${props.theme.colors.darkBlue};`
    }
  }}
`
export const EventDescription = styled(Text)`
`

export const TimeText = styled(Text)`
`

export const DateHeader = styled.h2`
  margin-top: 50px;
`
