import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const Text = styled.p`
  color: ${props => props.theme.colors.darkBlue};
`

const OutsideBox = styled.div`
  margin-left: 20%;
  margin-right: 20%;
`

const EventBox = styled.div`
  border: 2px solid #102e55;
  border-radius: 10px;
  padding: 20px;
`

const handleEventType = type => {
  switch (type) {
    case "workshop":
      return "color: $(props => props.theme.darkBeige)"
    case "activity":
      return "color: $(props => props.theme.tartOrange)"
    case "tech_talk":
      return "color: $(props => props.theme.teal)"
    default:
      return "color: $(props => props.theme.darkBlue);"
  }
}

const EventTitle = styled.h3`
  ${({ type }) => handleEventType(type)};
`
const EventDescription = styled(Text)`
`

const TimeText = styled(Text)`
`

const DateHeader = styled.h2`
  text-align: center;
  margin-top: 50px;
`



function App() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    async function fetchEvents() {
      fetch('https://api.hackthenorth.com/v3/graphql?query={ events { id name event_type permission start_time end_time description speakers { name profile_pic } public_url private_url related_events } }')
        .then(response => response.json())
        .then(data => {
          console.log(data)
          console.log(data.data.events)
          // sort by start_time
          setEvents(data.data.events.sort((a, b) => (a.start_time - b.start_time)))
        });
    }

    fetchEvents();
  }, [])

  var currentDate = -1;

  return (
    <div className="App">
      <header className="App-header">
        {/* <img src={logo} className="App-logo" alt="logo" /> */}
        <OutsideBox>
          {events.map((event, i) => {
            var start = new Date(event.start_time);
            var end = new Date(event.end_time);
            var startTime = start.toLocaleString("en-CA", {hour: 'numeric', minute: '2-digit'});
            var endTime = end.toLocaleString("en-CA", {hour: 'numeric', minute: '2-digit'});
            var dateText;
            if (currentDate !== start.getDate()) {
              currentDate = start.getDate();
              dateText = start.toLocaleString("en-CA", {weekday: 'long', month: 'long', day: 'numeric'});
            }
            return (
              <>
                {dateText && <DateHeader>{dateText}</DateHeader> }
                <EventBox key={i}>
                  <EventTitle type={event.event_type}>{event.name}</EventTitle>
                  <TimeText>{startTime} - {endTime}</TimeText>
                  <EventDescription>{event.description}</EventDescription>
                </EventBox>
              </>
            )
          })}
        </OutsideBox>

      </header>
    </div>
  );
}

export default App;
