import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import logo from './logo.svg';
import './App.css';

// const Eventbox = styled(div)`

// `
// const EventTitle = styled(h2)`

// `

function App() {
  const [events, setEvents] = useState([]);

  useEffect(() => {
    async function fetchEvents() {
      fetch('https://api.hackthenorth.com/v3/graphql?query={ events { id name event_type permission start_time end_time description speakers { name profile_pic } public_url private_url related_events } }')
        .then(response => response.json())
        .then(data => {
          console.log(data)
          console.log(data.data.events)
          // sort by start_time
          setEvents(data.data.events.sort((a, b) => (a.start_time - b.start_time)))
        });
    }

    fetchEvents();
  }, [])


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          {events.map((event, i) => {
            var start = new Date(event.start_time);
            var end = new Date(event.end_time);
            return (
              <div key={i}>
                <h2>{event.name}</h2>
                <p>{start.toString()}</p>
                <p>{end.toString()}</p>
              </div>
            )
          })}
        </div>

        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
