import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import logo from './logo.svg';
import './App.css';

const EventBox = styled.div`
  margin-left: 20%;
`
const EventTitle = styled.h2`
  color: white;
`

const TimeText = styled.p`
  color: white;
`

function App() {
  const [events, setEvents] = useState([]);
  const [date, setDate] = useState(-1);

  useEffect(() => {
    async function fetchEvents() {
      fetch('https://api.hackthenorth.com/v3/graphql?query={ events { id name event_type permission start_time end_time description speakers { name profile_pic } public_url private_url related_events } }')
        .then(response => response.json())
        .then(data => {
          console.log(data)
          console.log(data.data.events)
          // sort by start_time
          setEvents(data.data.events.sort((a, b) => (a.start_time - b.start_time)))
        });
    }

    fetchEvents();
  }, [])


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <div>
          {events.map((event, i) => {
            var start = new Date(event.start_time);
            var end = new Date(event.end_time);
            console.log("month:", start.getMonth())
            console.log("date:", start.getDate())
            if (date == 0) {
              console.log("WOOHOO")
            }
            return (
              <EventBox key={i}>
                <EventTitle>{event.name}</EventTitle>
                <TimeText>{start.toString()}</TimeText>
                <TimeText>{end.toString()}</TimeText>
              </EventBox>
            )
          })}
        </div>

      </header>
    </div>
  );
}

export default App;
