import styled, { css } from 'styled-components'

const Box = styled.div`
  ${props => props.outside && css`
    margin: 0% 15% 10% 15%;
  `}

  ${props => props.inside && css`
    border: 2px solid ${props => props.theme.colors.darkBlue};
    border-radius: 10px;
    overflow: hidden;
  `}

  ${props => props.login && css`
    float: right;
    margin-top: 8%;
    width: 20%;
  `}
`

export default Box
