import styled, { css } from 'styled-components'

const Anchor = styled.a`
  color: ${props => props.theme.colors.darkBlue}

  ${props => props.date && css`
    &:hover {
      color: ${props.theme.colors.blue};
    }
  `}
`

export default Anchor
