import styled, { css } from 'styled-components';

const Text = styled.p`
  color: ${props => props.theme.colors.darkBlue};

  ${props => props.bold && css`
    font-weight: bold;
  `}

  ${props => props.error && css`
    color: red;
  `}
`

export default Text
