  //     "public_url": "",
  //     "private_url": "",
  //     "related_events": ""}]
  // );
  const [events, setEvents] = useState([]);

  useEffect(() => {
    async function fetchEvents() {
      fetch('https://api.hackthenorth.com/v3/graphql?query={ events { id name event_type permission start_time end_time description speakers { name profile_pic } public_url private_url related_events } }')
        .then(response => response.json())
        .then(data => {
          console.log(data)
          console.log(data.data.events)
          setEvents(data.data.events)
          // setEvents(JSON.stringify(data.data.event.name))
          // setEvents(["hello", "goodbye"])
        });
    }

    fetchEvents();
  }, [])

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
          Edit <code>src/App.js</code> and save to reload.
        </p>
        <div>{events}</div>
        {/* <ol> */}
        {/*   {events.map((event, i) => { */}
        {/*       return ( */}
        {/*         <li key={i}> */}
        {/*           <p>event.name</p> */}
        {/*         </li> */}
        {/*       ) */}
        {/*     })} */}
        {/* </ol> */}

        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
