import styled, { css } from 'styled-components'
import {
  Anchor,
  Box,
  DateHeader,
  EventBox,
  EventTitle,
  Image,
  Header,
  Text,
} from './basics'

function Event(props) {
  return (
    Object.keys(props.groupedByDateEvents).map((day, dayIndex) => {
    return (
      <>
        <Header>
          <DateHeader>{day}</DateHeader>
        </Header>
        <Box inside>
          {props.activeEvents.length === 0 &&
            <EventBox>
              <Text>Whoops, it looks like there are no matching events.</Text>
            </EventBox>
          }
          {props.groupedByDateEvents[day].map((event, eventIndex) => {
            var start = new Date(event.start_time);
            var end = new Date(event.end_time);
            var startTime = start.toLocaleString("en-CA", {hour: 'numeric', minute: '2-digit'});
            var endTime = end.toLocaleString("en-CA", {hour: 'numeric', minute: '2-digit'});
            var longDate = (new Date(event.start_time)).toLocaleString("en-CA", {weekday: 'long', month: 'long', day: 'numeric'});
            return (
              <EventBox id={event.id} key={event.id} first={eventIndex === 0 ? true : false}>
                <a onClick={ (e) => props.handleEventClick(e, event.id) }>
                  <Text type={event.event_type}>{props.getEventType(event.event_type)}</Text>
                  <EventTitle type={event.event_type}>{event.name}</EventTitle>
                  <Text bold>{longDate}</Text>
                  <Text>{startTime} - {endTime}</Text>
                </a>
                {(props.expandedEvents.includes(event.id)) &&
                  <>
                    {event.speakers.length > 0 &&
                      <Text><strong>Speakers:</strong>
                        {event.speakers.map((speaker, speakerIndex) => {
                          var name = speaker.name;
                          if (speakerIndex === 0) {
                            name = " "+speaker.name;
                          } else {
                            name = ", "+speaker.name
                          }
                          return (
                            <>
                              {speaker.profile_pic && <Image src={speaker.profile_pic} alt={"profile picture of "+speaker.name} />}
                              {name}
                            </>
                          )
                        })}
                      </Text>
                    }
                    <Text>{event.description}</Text>
                    {event.related_events.length > 0 && <Text bold>Related Events:</Text>}
                    {event.related_events.map((relatedEventId, relatedEventIndex) => {
                      var targetEvent = (props.events.find(eve => {
                        return eve.id === relatedEventId
                      }))
                      if (!props.loggedIn && targetEvent.permission === "private") {
                        return
                      } else {
                        return (
                          <Anchor href={"#"+relatedEventId}>
                            <Text>{targetEvent.name}</Text>
                          </Anchor>
                        )
                      }
                    })}
                  </>
                }
              </EventBox>
            )
          })}
        </Box>
      </>
    )
  }))
}

export default Event
